/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useHistory } from 'react-router-dom'
import ReactApexChart from "react-apexcharts";
import {
    Form,
    Container,
    Col,
    Row,
    Button,
    FormGroup,
    Label,
    Input,
} from "reactstrap"
import catSubcat from '../jsondropdown/cat_subcat.json'
import cityState from '../jsondropdown/city_state.json'
import CapFirst from '../utils/firstletterCap'
import Sidebar from '../components/sidebar/Sidebar';

const Home = () => {
    let history = useHistory();

    const [dataframedata, setDataframeData] = useState([]);
    const [salesvalue, setSalesValue] = useState([]);
    const [category, setCatgory] = useState("Technology");
    const [subcategory, setSubCatgory] = useState("true");
    const [citylabels, setCityLables] = useState([]);
    const [cityvalue, setCityValue] = useState([]);
    const [statevalue, setStateValue] = useState([]);
    const [statelabels, setStateLables] = useState([]);
    const [showChart, setShowChart] = useState(false)

    useEffect(() => {
        load();
    }, [])


    const load = async () => {

    }

    const formSubbmit = async () => {
        const data = {
            cat: category,
            subCat: subcategory,
        }
        await axios.post("/top10", data)
            .then((result) => {
                setCityLables(result.data.City_Sales_Lable)
                setCityValue(result.data.City_Sales_Value)
                setStateLables(result.data.State_Sales_Lable)
                setStateValue(result.data.State_Sales_Value)
                setSubCatgory("true")
                setShowChart(true);
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const getSubcategory = (category) => {
        if (category !== "true") {
            let subcategory = catSubcat[category];
            return subcategory.map((subcat, idx) => {
                return (
                    <option key={idx} value={subcat}>
                        {CapFirst(subcat)}
                    </option>
                );
            });
        }
    };
    const categories = () => {
        var keys = Object.keys(catSubcat);
        return keys.map((cat, idx) => {
            return (
                <option key={idx} value={cat}>
                    {CapFirst(cat)}
                </option>
            );
        });
    };

    return (
        <Sidebar />
    )
}


export default Home;
